targets.expr:
  (
      ikuai_network_recv_kbytes_per_second{id=~"device/.*", job="$jobName", instance="$instance"}
    +
      ikuai_network_send_kbytes_per_second{id=~"device/.*", job="$jobName", instance="$instance"}
  )
* on (id) group_left (mac, comment)
  (max by (id, mac, comment) (ikuai_device_info{id=~"device/.*", job="$jobName", instance="$instance"}))
